import React, { useState, useEffect } from "react";
import "../styling/Hobbies.css";

const Hobbies = () => {
  const [stats, setStats] = useState({});
  const [videos, setVideos] = useState({});
  const [error, setError] = useState(null);
  const [toggleState, setToggleState] = useState({});

  const imagePaths = [
    "/misc/Baseball/Images/Little_Pitch.JPG",
    "/misc/Baseball/Images/Cooperstown.JPEG",
    "/misc/Baseball/Images/Westfield_Pitch.JPEG",
    "/misc/Baseball/Images/Team_Pic.PNG",
    "/misc/Baseball/Images/MVP.JPG",
  ];

  const years = ["2014", "2015", "2016", "College"];

  const fetchStats = async (path) => {
    try {
      const response = await fetch(path);
      if (!response.ok) throw new Error(`Failed to fetch stats from ${path}`);
      const text = await response.text();
      return text.split("\n").filter((line) => line);
    } catch (err) {
      setError(err.message);
      return [];
    }
  };

  const fetchVideoData = async () => {
    try {
      const response = await fetch("/misc/Baseball/video_file_list.json");
      if (!response.ok) throw new Error("Failed to fetch video list.");
      return await response.json();
    } catch (err) {
      setError(err.message);
      return {};
    }
  };

  useEffect(() => {
    const loadContent = async () => {
      const statsData = {};
      for (const year of years.filter((y) => y !== "College")) {
        statsData[year] = await fetchStats(
          `/misc/Baseball/Stats/game_data_${year}_stats.txt`
        );
      }
      setStats(statsData);
      const videoData = await fetchVideoData();
      setVideos(videoData);
    };
    loadContent();
  }, []);

  const toggleYear = (year) => {
    setToggleState((prevState) => ({
      ...prevState,
      [year]: !prevState[year],
    }));
  };

  const renderVideos = (videoList, basePath) => (
    <div className="videos">
      {videoList?.length > 0 ? (
        videoList.map((video, index) => (
          <video
            key={index}
            controls
            className="video"
            src={`${basePath}/${video}`}
          >
            Your browser does not support the video tag.
          </video>
        ))
      ) : (
        <p>No videos available</p>
      )}
    </div>
  );

  const renderYearContent = (year) => {
    if (year === "College") {
      return renderVideos(videos[year] || [], `/misc/Baseball/${year}`);
    }

    const categories = ["Stats", "Singles", "Doubles", "Triples", "Homeruns"];
    return (
      <>
        {categories.map((category) => (
          <div key={category} className="category-section">
            <h3>{category}</h3>
            {category === "Stats"
              ? stats[year]?.length > 0
                ? stats[year].map((line, index) => <li key={index}>{line}</li>)
                : "No stats available"
              : renderVideos(
                  videos[`${year}/${category}`] || [],
                  `/misc/Baseball/${year}/${category}`
                )}
          </div>
        ))}
      </>
    );
  };

  return (
    <div className="container">
      <div className="image-gallery">
        {imagePaths.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Photo ${index + 1}`}
            className="gallery-image"
          />
        ))}
      </div>

      {years.map((year) => (
        <div key={year} className="year-section">
          <button onClick={() => toggleYear(year)}>
            {toggleState[year] ? `Close ${year}` : `Open ${year}`}
          </button>
          {toggleState[year] && renderYearContent(year)}
        </div>
      ))}

      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Hobbies;
